import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import { MyContext } from "../MyContext";

export default function ContactUs(level) {
  console.log(level.level);
  const form = useRef();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [formClass, setFormClass] = useState("");
  const [textClass, setTextClass] = useState("hide-text");

  const sendEmail = (e) => {
    e.preventDefault();

    setTextClass("show-text");
    setName("Thanks!");
    setEmail("Thanks!");
    setFormClass("hide-text");

    emailjs
      .sendForm(
        "service_y9kwcoa",
        "template_utaehz6",
        form.current,
        "user_O0EicnClTqFEK6Kl07ODl"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <MyContext.Consumer>
      {({ course, media }) => {
        return (
          <>
            <div className="contact-page">
              <form ref={form} onSubmit={sendEmail} className="form-format">
                <div className={formClass}>
                  <h3 className="complete-test-text">
                    To complete the test, please submit your name and email.
                  </h3>
                  <div>
                    <label>Name: </label>
                    <div className="form-section"></div>
                    <input
                      type="text"
                      name="name"
                      id="name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                    />
                  </div>

                  <label>Email: </label>
                  <div className="form-section">
                    <input
                      type="email"
                      name="email"
                      id="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>
                  <div className="form-section visually-hidden">
                    <label>Course Recommendations: </label>
                    <input
                      className="course-box"
                      type="text"
                      name="courseResults"
                      id="courseResults"
                      value={course
                        .map((individualCourse) => individualCourse)
                        .join(", ")}
                      readOnly
                    />
                  </div>

                  <div className="form-section">
                    <label>Media: </label>
                    <input
                      className="media-box"
                      type="text"
                      name="mediaResults"
                      id="mediaResults"
                      value={media
                        .map((individualMedia) => individualMedia)
                        .join(", ")}
                      readOnly
                    />
                  </div>

                  <button
                    className="submit-btn"
                    type="submit"
                    value="Submit"
                    readOnly
                  >
                    Submit
                  </button>
                </div>
                <p className={`contact-page-thanks-text ${textClass}`}>
                  Thanks! You have successfully submitted your results. Please
                  follow the link below to see your course recommendations:
                </p>

                <div className="link-wrapper">
                  <a
                    href={`https://www.carl-schurz-haus.de/nc/sprachkurse/erwachsene.html?kathaupt=1&katid=67&katvaterid=64&katname=${level.level}`}
                    target="_blank"
                    rel="noreferrer"
                    className={`recommendations-link {textClass}`}
                  >
                    <p className={textClass}>
                      {level.level} course recommendations
                    </p>
                  </a>
                </div>
              </form>
            </div>
          </>
        );
      }}
    </MyContext.Consumer>
  );
}
